mapper.update:
update termination
set termination_status = #{status}
where contract_id = #{contract_id}

update termination
set apply_date = #{apply_date}
where contract_id = #{contract_id}

UPDATE termination
SET apply_date         = #{apply_date},
    termination_fee    = #{termination_fee},
    origin_contract_id = #{origin_contract_id},
    termination_status = #{termination_status}
WHERE contract_id = #{contract_id};
